(function(root){"use strict";var SPACE=/\s/g;var LESS_THAN=/\>/g;var MORE_THAN=/</g;function escape(str){return str.replace(SPACE,"&nbsp;").replace(LESS_THAN,"&lt;").replace(MORE_THAN,"&gt;")}var spacerStyle="box-sizing:content-box;display:inline-block;height:0;overflow:hidden;position:absolute;top:0;visibility:hidden;white-space:nowrap";function autosizeInput(elem,opts){var spacer=document.createElement("div");document.body.appendChild(spacer);var elemStyle=window.getComputedStyle(elem);var cssText=spacerStyle+";font-family:"+elemStyle.fontFamily+";font-size:"+elemStyle.fontSize;spacer.style.cssText=cssText;function set(str){spacer.innerHTML=escape(str);var width=window.getComputedStyle(spacer).width;elem.style.width=width;return width}var width=set(elem.value||elem.getAttribute("placeholder")||"");elem.addEventListener("input",function(e){set(e.target.value)});if(opts&&opts.minWidth){spacer.style.minWidth=width}}if(typeof module=="object"){module.exports=autosizeInput}else{root.autosizeInput=autosizeInput}})(this);