(function(root){"use strict";var SPACE=/\s/g;var LESS_THAN=/\>/g;var MORE_THAN=/</g;function escape(str){return str.replace(SPACE,"&nbsp;").replace(LESS_THAN,"&lt;").replace(MORE_THAN,"&gt;")}var ghostStyle="box-sizing:content-box;display:inline-block;height:0;overflow:hidden;position:absolute;top:0;visibility:hidden;white-space:nowrap";function autosizeInput(elem,opts){elem.style.boxSizing="content-box";var ghost=document.createElement("div");document.body.appendChild(ghost);var elemStyle=window.getComputedStyle(elem);ghost.style.cssText=ghostStyle+";font-family:"+elemStyle.fontFamily+";font-size:"+elemStyle.fontSize;function set(str){str=str||elem.value||elem.getAttribute("placeholder")||"";ghost.innerHTML=escape(str);var width=window.getComputedStyle(ghost).width;elem.style.width=width;return width}elem.addEventListener("input",function(){set()});var width=set();if(opts&&opts.minWidth&&width!=="0px"){ghost.style.minWidth=width}return set}if(typeof module=="object"){module.exports=autosizeInput}else{root.autosizeInput=autosizeInput}})(this);