(function(){"use strict";var SPACE=/\s/g;var LESS_THAN=/>/g;var MORE_THAN=/</g;function escape(str){return str.replace(SPACE,"&nbsp;").replace(LESS_THAN,"&lt;").replace(MORE_THAN,"&gt;")}var ghost=document.createElement("div");ghost.style.cssText="box-sizing:content-box;display:inline-block;height:0;overflow:hidden;position:absolute;top:0;visibility:hidden;white-space:nowrap;";document.body.appendChild(ghost);function autosizeInput(elem,opts){elem.style.boxSizing="content-box";var elemStyle=window.getComputedStyle(elem);var elemCssText="font-family:"+elemStyle.fontFamily+";font-size:"+elemStyle.fontSize;function set(str){str=str||elem.value||elem.getAttribute("placeholder")||"";ghost.style.cssText+=elemCssText;ghost.innerHTML=escape(str);var width=window.getComputedStyle(ghost).width;elem.style.width=width;return width}elem.addEventListener("input",function(){set()});var width=set();if(opts&&opts.minWidth&&width!=="0px"){elem.style.minWidth=width}return set}if(typeof module=="object"){module.exports=autosizeInput}else{window.autosizeInput=autosizeInput}})();